import { AutoForm, TextField, SubmitField } from "uniforms-mui"; 
import { bridge as schema} from "../api/resolutions/bridge";
import React from 'react';
import gql from 'graphql-tag';
import { graphql } from 'react-apollo';



const createContact = gql`
    mutation createContact($firstname: String!, $lastname: String!) {
        createContact(firstname: $firstname, lastname: $lastname) {
            _id
            firstname
            lastname
        }
    }
`;



class ContactForm extends React.Component {


  submitForm = (model) => {

    const { firstname, lastname } = JSON.parse(model);  
    console.log(JSON.parse(model));
    console.log(firstname);
    console.log(lastname);

    this.props.createContact({
        variables: {
            firstname:firstname,
            lastname: lastname,
        }
    }).catch(error =>{
        console.log(error);
    });
  };

  render(){

      return (
            <div>
                <AutoForm
                schema={schema}
                onSubmit={(model) => this.submitForm(JSON.stringify(model))}

              />
                /* <TextField name="firstName" />
                <ErrorField children={children} name="firstName" />;
                <TextField name="lastName" />
                <ErrorField children={children} name="lastName" />;
              <AutoForm/> */

            </div>

      );
  }
}


export default graphql(createContact, {
    name: "createContact",
    options: {
        refetchQueries: ["Contacts"]
    }
})(ContactForm)
